import { Component, OnInit } from '@angular/core';
import DataTable from 'datatables.net-dt';
import { ClientesService } from '../../services/clientes.service';
import { clientes } from '../../models/clientes';
@Component({
  selector: 'app-listaclientes',
  templateUrl: './listaclientes.component.html',
  styleUrls: ['./listaclientes.component.css']
})



export class ListaclientesComponent implements OnInit{

  clientes:clientes[];

  constructor(private clientesServicio: ClientesService){}

  ngOnInit(): void {
    this.crearDatatable();
    this.listarClientes();
  }

  crearDatatable() {
    setTimeout(() => {
      const table= new DataTable("#tabla_clientes",{

      });
    }, 1);
  }

  listarClientes(){
    this.clientesServicio.listarClientes().subscribe({
      next: (res) => {
        this.clientes=res;
      },
      error: (errorData) => {
       alert("Error al cargar los datos")
      },
      complete: () => {
        alert("importacion completada");
      }
    });
  }
}
<h2 class="text-center">Lista de clientes</h2>
<table id="tabla_clientes" class="table">
    <thead  class="cell-border table-striped stripe hover">
        <tr>
            <th>Nombre</th>
            <th>Apellido</th>
            <th>Dirección</th>
            <th>Teléfono</th>
            <th>Estado</th>
            <th>Acciones</th>
        </tr>
    </thead>
    <tbody>
        <tr *ngFor= "let cliente of clientes">
            <td>{{cliente.nombre}}</td>
            <td>{{cliente.apellido}}</td>
            <td>{{cliente.direccion}}</td>
            <td>{{cliente.telefono}}</td>
            <td>{{cliente.fechaRegistro}}</td>
            <td><button class="btn btn-success"></button> <button class="btn btn-danger"></button></td>
        </tr>
    </tbody>
</table>
