import { Component } from '@angular/core';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent {
  sidenav= true;

  cambiarEstadoMenu(){
    this.sidenav=!this.sidenav;
  }
}
<app-header></app-header>
<main class="contenedorMain">
    <div class="sidevar"[ngClass]="{show:sidenav}"> 
        <div class="todo">
            <app-sidevar></app-sidevar>
        </div>
    </div>
    <div class="container-fluid contenido containerDerecha" [ngClass]="{show:sidenav}">
        <div class="contenido__proyecto">
            <router-outlet></router-outlet>
        </div>
    </div>

    <div class="botonMenu">
          <svg  (click)="cambiarEstadoMenu()" width="16" height="16" fill="currentColor" class="bi bi-list" viewBox="0 0 16 16">
            <path fill-rule="evenodd" d="M2.5 12a.5.5 0 0 1 .5-.5h10a.5.5 0 0 1 0 1H3a.5.5 0 0 1-.5-.5m0-4a.5.5 0 0 1 .5-.5h10a.5.5 0 0 1 0 1H3a.5.5 0 0 1-.5-.5m0-4a.5.5 0 0 1 .5-.5h10a.5.5 0 0 1 0 1H3a.5.5 0 0 1-.5-.5"/>
          </svg>
    </div> 
</main>
