import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { ClientesService } from '../../services/clientes.service';
import { Router } from '@angular/router';
import DataTable from 'datatables.net-dt';
import { proyectos } from '../../models/proyectos';

@Component({
  selector: 'app-listarproyectos',
  templateUrl: './listarproyectos.component.html',
  styleUrls: ['./listarproyectos.component.css']
})
export class ListarproyectosComponent {

  proyectos:proyectos[];

  constructor(private clientesServicio: ClientesService, private router:Router){}

  ngOnInit(): void {
    this.listarProyectos();
  }

  crearDatatable() {
    setTimeout(() => {
      const table= new DataTable("#tabla_clientes",{

      });
    }, 1);
  }

  listarProyectos(){
    this.clientesServicio.listarClientes().subscribe({
      next: (res) => {
        this.proyectos=res;
      },
      error: (errorData) => {
      alert("Error al cargar los datos")
      },
      complete: () => {
        alert("importacion completada");
        this.crearDatatable();
      }
    });
  }

  verProyecto(id:number){
    this.router.navigate(["verproyecto",id]);
  }



  crearProyecto(){
    this.router.navigate([""]);
  }

  editarProyecto(id:number){
    this.router.navigate(["",id]);
  }

  eliminarProyecto(id:number){
    this.clientesServicio.eliminarCliente(id).subscribe({
      next:(res)=>{

      },
      error:(errorData)=>{
        alert("Error al eliminar el cliente");
      },
      complete:()=>{
        alert("Cliente eliminado");
      }
    });
  }
}
<div class="titulo">
    <h2 class="titulo_texto text-center">Lista de clientes</h2>
    <button (click)="crearProyecto()" class="btn btn-primary">Agregar Cliente</button>
</div>

<table id="tabla_clientes" class="table">
    <thead  class="cell-border table-striped stripe hover">
        <tr>
            <th>Nombre</th>
            <th>Apellido</th>
            <th>Descripción</th>
            <th>Acciones</th>
        </tr>
    </thead>
    <tbody>
        <tr *ngFor="let proyecto of proyectos">
            <td>{{proyecto.id}}</td>
            <td>{{proyecto.nombre}}</td>
            <td>{{proyecto.descripcion}}</td>
            <td><button (click)="editarProyecto(proyecto.id)" class="btn btn-success">Editar</button> <button (click)="eliminarProyecto(proyecto.id)" class="btn btn-danger">Eliminar</button></td>
        </tr>
    </tbody>
</table>
