import { Component } from '@angular/core';

@Component({
  selector: 'app-sidevar',
  templateUrl: './sidevar.component.html',
  styleUrls: ['./sidevar.component.css']
})
export class SidevarComponent {
  ngOnInit(){
  }
  slider_1= true;
  slider_2= true;
  slider_3= true;
  cambiarEstadoMenu(id:number){
    switch(id){
      case 1:
        this.slider_1=!this.slider_1;
      break;
      case 2:
        this.slider_2=!this.slider_2;
      break;
      case 3:
        this.slider_3=!this.slider_3;
      break;
    }
  }
}
<div class="todo">    
    <div class="sidevar_titulo">
        <h6 class="sidevar_titulo--h5 text-center">Dirección de Sistemas y Tecnología - Área Desarrollo de software </h6>
    </div>
    <div class="imagenPerfil">
    </div>
    <div class="sidevar__menu">
        <ul  class="dropdown">
            <li class="dropdown__list">
                    <ul class="dropdown__sub" [ngClass]="{show:slider_1}">
                        <li class="dropdown__li" routerLink="/AulasEspejo/listarConvocatorias">
                            <a class="sidevar__menu--a" ><i class="sidevar__menu--i bi bi-arrow-right-short"></i>Transacciones</a>
                        </li>
                        <li class="dropdown__li" routerLink="/AulasEspejo/seguimientoSolicitudes">
                            <a class="sidevar__menu--a" ><i class="sidevar__menu--i bi bi-arrow-right-short"></i>Resumen de transacciones</a>
                        </li>
                        <li class="dropdown__li" routerLink="/AulasEspejo/plantillas">
                            <a class="sidevar__menu--a" ><i class="sidevar__menu--i bi bi-arrow-right-short"></i>Administrador</a>
                        </li>
                    </ul>
            </li>
        </ul>

    </div>

