import { Component, OnInit } from '@angular/core';
import { ClientesService } from '../../services/clientes.service';
import { Router } from '@angular/router';
import { proyectos } from '../../models/proyectos';

@Component({
  selector: 'app-verproyecto',
  templateUrl: './verproyecto.component.html',
  styleUrls: ['./verproyecto.component.css']
})
export class VerproyectoComponent implements OnInit{
  idproyecto:number;
  proyecto:proyectos;
  constructor(private proyectoServicio:ClientesService,private router:Router){}
  ngOnInit(): void {
    this.importarProyecto()
  }

  importarProyecto(){
    this.proyectoServicio.verProyecto(this.idproyecto).subscribe({
      next:(res)=>{
        this.proyecto=res;
      },
      error: (errorData)=> {},

      complete: () => {
        alert("Datos cargados");
      }
    });
  }
}
<div class="titulo">
    <h2 class="text-center">Convocatoria numero {{proyecto.id}}</h2>
</div>

<div class="container row">
    <div class="form-group col-md-6">
        <label for="nombre">Nombre:</label>
        <input class="form-control" type="text">
    </div>

    <div class="form-group col-md-6">
        <label for="descripcion">Descripción:</label>
        <input class="form-control" type="text">
    </div>

    <div class="form-group col-md-6">
        <label for="fecha">Fecha de inicio:</label>
        <input class="form-control" type="date">
    </div>

    <div class="form-group col-md-6">
        <label for="url">URL:</label>
        <input class="form-control" type="text" >
    </div>

    <div class="form-group col-md-6">
        <label for="">Creado por:</label>
        <input class="form-control" type="text" >
    </div>

</div>
