import { Component, OnInit } from '@angular/core';
import { ClientesService } from '../../services/clientes.service';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-crearproyecto',
  templateUrl: './crearproyecto.component.html',
  styleUrls: ['./crearproyecto.component.css']
})
export class CrearproyectoComponent implements OnInit{

  idusuario:number;

  constructor(private proyectosServicio:ClientesService){}

  ngOnInit(): void {
    this.obtenerUsuario;
  }

  obtenerUsuario(){
    this.idusuario=1;
  }

  formproyecto = new FormGroup({
    'nombre': new FormControl('', Validators.required),
    'descripcion': new FormControl('', Validators.required),
    'url': new FormControl('', Validators.required),
    'fechacreacion': new FormControl(null, Validators.required),
    'idusuario': new FormControl(0)
  });

  crearProyecto(){
    if(this.formproyecto.valid){
      this.formproyecto.get("idusuario")?.setValue(this.idusuario);
      this.proyectosServicio.crearCliente(this.idusuario,this.formproyecto.value).subscribe({
        next:(res)=>{
        },
        error:(errorData)=>{
          alert("Error al crear el proyecto")
        },
        complete:()=>{
          alert("Proyecto creado");
        }
      });
    }
    else{
      this.formproyecto.markAllAsTouched();
      alert("Debes llenar todos los campos");
    }


  }
}
<div class="titulo">
    <h2 class="text-center">Agregar nuevo proyecto</h2>
</div>
<form [formGroup]="formproyecto">
    <div class="container row">
        <div class="form-group col-md-6">
            <label for="">Nombre:</label>
            <input class="form-control" type="text" formControlName="nombre">
        </div>

        <div class="form-group col-md-6">
            <label for="">Descripción:</label>
            <input class="form-control" type="text" formControlName="descripcion">
        </div>

        <div class="form-group col-md-6">
            <label for="">URL:</label>
            <input class="form-control" type="text" formControlName="url">
        </div>

        <div class="form-group col-md-6">
            <label for="">Fecha de inicio:</label>
            <input class="form-control" type="date" formControlName="fechacreacion">
        </div>
        <!--
        <div class="col-md-6">
            <label for="">Estado</label>
            <input type="text">
        </div>
        -->
    </div>

    <div class="buttons">
        <button class="btn btn-danger">Cancelar</button>
        <button (click)="crearProyecto()" class="btn btn-primary">Crear Proyecto</button>
    </div>
</form>
