import { Component, OnInit } from '@angular/core';
import { ClientesService } from '../../services/clientes.service';
import { ActivatedRoute, Router } from '@angular/router';
import { proyectos } from '../../models/proyectos';

@Component({
  selector: 'app-verproyecto',
  templateUrl: './verproyecto.component.html',
  styleUrls: ['./verproyecto.component.css']
})
export class VerproyectoComponent implements OnInit{
  idproyecto:number;
  proyecto:proyectos;
  constructor(private proyectoServicio:ClientesService,private router:Router, private route:ActivatedRoute){}
  ngOnInit(): void {
    this.idproyecto= this.route.snapshot.params["id"];
    this.importarProyecto()
  }

  importarProyecto(){
    this.proyectoServicio.verProyecto(this.idproyecto).subscribe({
      next:(res)=>{
        this.proyecto=res;
      },
      error: (errorData)=> {
        alert("Error al carga la informacion del proyecto");
      },
      complete: () => {
        alert("Datos cargados");
      }
    });
  }

  volverInicio(){
    this.router.navigate(["/dashboard/listaproyectos"]);
  }

  editarProyecto(){
    this.router.navigate(["/dashboard/editarproyecto/", this.idproyecto]);
  }
}
<div class="titulo">
    <h2 class="text-center">Convocatoria numero {{idproyecto}}</h2>
</div>

<div class="container row">
    <div class="form-group col-md-6">
        <label for="nombre">Nombre:</label>
        <input class="form-control" type="text" readonly value={{proyecto.nombre}}>
    </div>

    <div class="form-group col-md-6">
        <label for="url">URL:</label>
        <input class="form-control" type="text" readonly value={{proyecto.url}}>
    </div>

    <div class="form-group col-md-12">
        <label for="descripcion">Descripción:</label>
        <textarea class="form-control" type="text" readonly value={{proyecto.descripcion}}>

        </textarea>
    </div>

    <div class="form-group col-md-6">
        <label for="fecha">Fecha de inicio:</label>
        <input class="form-control" type="date" readonly value={{proyecto.fechaCreacion}}>
    </div>

    <div class="form-group col-md-6">
        <label for="">Creado por:</label>
        <input class="form-control" type="text" readonly value={{proyecto.id}}>
    </div>

    <div class="buttons form-group">
        <button (click)="volverInicio()" class="btn btn-danger">Cancelar</button>
        <button (click)="editarProyecto()" class="btn btn-primary">Editar</button>
    </div>
</div>
