import { Component, OnInit } from '@angular/core';
import { UsuarioService } from '../../services/usuario.service';
import { usuario } from '../../models/usuario';

@Component({
  selector: 'app-cliente',
  templateUrl: './cliente.component.html',
  styleUrls: ['./cliente.component.css']
})
export class ClienteComponent implements OnInit{

  infoUsuario:usuario;
  idUsuario:number;

  constructor(private usuariosService:UsuarioService){}

  ngOnInit(): void {
    this.cargarUsuario();
    this.importarDatosUsuario();
  }

  cargarUsuario(){
    this.idUsuario=1;
  }

  importarDatosUsuario(){
    this.usuariosService.obtenerUsuario(this.idUsuario).subscribe({
      next: (res) =>{
        this.infoUsuario=res;
      },
      error:(errorData)=>{
        alert("Error al cargar la información del usuario");
      },
      complete: () => {
        alert("Usuario cargado");
      }
    });
  }
}
<div class="cliente__container">
    <div class="cliente__container--head">
        <h1>{{infoUsuario.nombre}}</h1>
        <b>Admin</b>
    </div>
    <div class="accordion accordion-flush" id="accordionFlushExample">
        <div class="accordion-item">
          <h2 class="accordion-header">
            <button class="accordion-button collapsed" type="button" data-bs-toggle="collapse" data-bs-target="#flush-collapseOne" aria-expanded="false" aria-controls="flush-collapseOne">
              Información
            </button>
          </h2>
          <div id="flush-collapseOne" class="accordion-collapse collapse" data-bs-parent="#accordionFlushExample">
            <div class="accordion-body">{{infoUsuario.email}}</div>
          </div>
          <div id="flush-collapseOne" class="accordion-collapse collapse" data-bs-parent="#accordionFlushExample">
            <div class="accordion-body">{{infoUsuario.direccion}}</div>
          </div>
          <div id="flush-collapseOne" class="accordion-collapse collapse" data-bs-parent="#accordionFlushExample">
            <div class="accordion-body">{{infoUsuario.telefono}}</div>
          </div>
        </div>
        <div class="accordion-item">
          <h2 class="accordion-header">
            <button class="accordion-button collapsed" type="button" data-bs-toggle="collapse" data-bs-target="#flush-collapseTwo" aria-expanded="false" aria-controls="flush-collapseTwo">
              Opciones
            </button>
          </h2>
          <div id="flush-collapseTwo" class="accordion-collapse collapse" data-bs-parent="#accordionFlushExample">
            <div class="accordion-body">Actualizar Información</div>
          </div>
          <div id="flush-collapseTwo" class="accordion-collapse collapse" data-bs-parent="#accordionFlushExample">
            <div class="accordion-body">Cerrar Sesión</div>
          </div>
        </div>
      </div>
</div>
