import { Component } from '@angular/core';
import { transaction } from '../../models/transaction';
import { TransactionService } from '../../services/transaction.service';
import { ActivatedRoute, Router } from '@angular/router';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { category } from '../../models/category';
import { CategoryoService } from '../../services/category.service';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-update-transaction',
  templateUrl: './update-transaction.component.html',
  styleUrls: ['./update-transaction.component.css']
})
export class UpdateTransactionComponent {
  categories:category[];
  idtransaction:number;
  transaction:transaction= new transaction();
  constructor(private transactionService:TransactionService, private categoryService:CategoryoService, private router:Router, private route:ActivatedRoute){}

  ngOnInit(): void {
    this.idtransaction= this.route.snapshot.params["id"];
    this.loadTransaction();
  }

  loadTransaction(){
    this.transactionService.seeTransaction(this.idtransaction).subscribe({
      next:(res)=>{
        this.transaction=res;
        this.loadCategories();
        this.actualizarFormulario();
      },
      error: (errorData)=> {
        alert("Error al carga la información de la transacción");
      },
      complete: () => {

        alert("Datos cargados");
      }
    });
  }

  loadCategories(){
    this.categoryService.listCategories().subscribe({
      next:(res)=>{
        this.categories=res;
      },
      error:(errorData)=>{
        alert("Error al registrar la transaccion")
      },
      complete:()=>{
        alert("categorias Importadas");
      }
    });
  }


  formtransaction= new FormGroup({
      'type': new FormControl(this.transaction.type, Validators.required),
      'amount': new FormControl(this.transaction.amount, Validators.required),
      'categoryName': new FormControl(this.transaction.categoryName, Validators.required),
      'categoryId': new FormControl(this.transaction.categoryId),
      'date': new FormControl(this.transaction.date, Validators.required),
      'description': new FormControl(this.transaction.description, Validators.required),
      'id': new FormControl(0)
  });

  actualizarFormulario(){
    this.formtransaction.get("type")?.setValue(this.transaction.type);
    this.formtransaction.get("amount")?.setValue(this.transaction.amount);
    this.formtransaction.get("categoryName")?.setValue(this.transaction.categoryName);
    this.formtransaction.get("categoryId")?.setValue(this.transaction.categoryId);
    this.formtransaction.get("date")?.setValue(this.transaction.date);
    this.formtransaction.get("description")?.setValue(this.transaction.description);
    this.formtransaction.get("id")?.setValue(this.transaction.id);
    console.log(this.formtransaction.value);
  }

  get type() {
    return this.formtransaction.get('type') as FormControl;
  }

  get amount() {
    return this.formtransaction.get('amount') as FormControl;
  }

  get categoryName() {
    return this.formtransaction.get('categoryName') as FormControl;
  }

  get date() {
    return this.formtransaction.get('date') as FormControl;
  }


  backToList(){
    this.router.navigate(["/dashboard/listTransactions"]);
  }

  updateTransaction(){
    Swal.fire({
      title: '¿Estás seguro?',
      text: "Confirma si deseas editar la transacción",
      icon: 'question',
      showCancelButton: true,
      confirmButtonColor: '#007B3E',
      cancelButtonColor: '#d33',
      confirmButtonText: 'Si , crear',
      cancelButtonText: 'No, cancelar',
      reverseButtons: true,
      buttonsStyling: true
    }).then((result) => {
      if (result.isConfirmed) {
        if(this.formtransaction.valid){
          var idcategory:any = this.formtransaction.get('categoryName')?.value;
          this.formtransaction.get('categoryId')?.setValue(idcategory);
          this.formtransaction.get('id')?.setValue(this.idtransaction);
          this.transactionService.updateTransaction(this.idtransaction, this.formtransaction.value).subscribe({
            next:(res)=>{
            },
            error:(errorData)=>{
              alert("Error al editar la transacción")
            },
            complete:()=>{
              alert("Transacción editada");
            }
          });
        }
        else{
          this.formtransaction.markAllAsTouched();
          Swal.fire({
            title: "Error",
            text: "Debes llenar todos los capos obligatorios",
            icon: "warning",
            confirmButtonColor: '#007B3E'
          });
        }
      }});
  }
}
<div class="titulo">
    <h2 class="text-center">transacción numero {{idtransaction}}</h2>
</div>

<form [formGroup]="formtransaction">
    <div class="container row">
        <div class="form-group col-md-6">
            <label for="nombre">Tipo de transacción</label>
            <input class="form-control" type="text" formControlName="type">
            <div class="alert alert-danger mt-2" *ngIf="type.invalid && (type.dirty || type.touched)">
                Este es un campo obligatorio
            </div>
        </div>

        <div class="form-group col-md-6">
            <label for="url">Cantidad</label>
            <input class="form-control" type="text" formControlName="amount">
            <div *ngIf="amount.invalid && (amount.dirty || amount.touched)" class="alert alert-danger">
                <div *ngIf="amount.errors?.['required']">Este es un campo obligatorio.</div>
                <div *ngIf="amount.errors?.['pattern']">
                    Solo puedes incluir valores númericos o decimales
                </div>
            </div>
        </div>

        <div class="form-group col-md-6">
            <label for="descripcion">Categoría:</label>
            <select  class="form-select modal__input" formControlName="categoryName">
                <option *ngFor="let category of categories" value={{category.id}}>{{category.name}}</option>
            <!--   <div class="alert alert-danger" *ngIf="email.invalid && (email.dirty || email.touched)">
                <div *ngIf="email.errors?.['minlength']">
                    Digite su email por favor
                </div>
                <div *ngIf="email.errors?.['pattern']">
                    Por favor digite el email con el siguiente formato : user100@gmail.com
                </div>
            </div>-->
            </select>
            <div class="alert alert-danger mt-2" *ngIf="categoryName.invalid && (categoryName.dirty || categoryName.touched)">
                Este es un campo obligatorio
            </div>
        </div>

        <div class="form-group col-md-6">
            <label for="fecha">Fecha de transacción:</label>
            <input class="form-control" type="date" formControlName="date">
            <div class="alert alert-danger mt-2" *ngIf="date.invalid && (date.dirty || date.touched)">
                Este es un campo obligatorio
            </div>
        </div>

        <div class="form-group col-md-6">
            <label for="">Descripción:</label>
            <input class="form-control" type="text"  formControlName="description">
        </div>
        <div class="buttons form-group">
            <button (click)="backToList()" class="btn btn-primary">Volver</button>
            <button (click)="updateTransaction()" class="btn btn-success">Editar</button>
        </div>
    </div>
</form>
