import { Component } from '@angular/core';
import { TransactionService } from '../../services/transaction.service';
import { Router } from '@angular/router';
import { transaction } from '../../models/transaction';
import DataTable from 'datatables.net-dt';
import { CategoryoService } from '../../services/category.service';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { category } from '../../models/category';
import Swal from 'sweetalert2';
import { NgxSpinnerService } from 'ngx-spinner';

@Component({
  selector: 'app-transactions-list',
  templateUrl: './transactions-list.component.html',
  styleUrls: ['./transactions-list.component.css']
})
export class TransactionsListComponent {
  table:any;
  transactions:transaction[];

  constructor(private transactionService: TransactionService, private categoryService:CategoryoService, private router:Router, private spinner:NgxSpinnerService){}

  ngOnInit(): void {
    this.spinner.show();
    this.listTransactions();
  }

  //LISTAR TRANSACCIONES


  creteDatatable() {
    setTimeout(() => {
      this.table= new DataTable("#tabla_clientes",{
        language: {
          url: '/assets/es-ES.json',
        },
        columnDefs: [
          {
            targets: "_all",
            className: 'dt-center'
          }
        ],
        paging: true,
        pagingType: 'simple',
        layout:{
          topStart: 'search',
          topEnd: 'pageLength',
          bottomStart: 'paging',
          bottomEnd: 'info'
        },
      });
    }, 1000);
  }

  listTransactions(){
    this.transactionService.listarTransactions().subscribe({
      next: (res) => {
        this.transactions=res;
      },
      error: (errorData) => {
        Swal.fire({
          title: "Error",
          text: "Error al cargar la lista de transacciones",
          icon: "error",
          confirmButtonColor: '#007B3E'
        });
        this.spinner.hide();
      },
      complete: () => {
        this.loadCategories();

        this.creteDatatable();
        this.spinner.hide();

      }
    });
  }

  //Crear Transacciones

  createTransactionModal(){
  }


  formtransaction = new FormGroup({
    'type': new FormControl('', Validators.required),
    'amount': new FormControl('', [Validators.required, Validators.pattern(/^\d*\.?\d*$/)]),
    'categoryId': new FormControl(null, Validators.required),
    'description': new FormControl('', Validators.required),
    'date': new FormControl(null, Validators.required)
  });

  get type() {
    return this.formtransaction.get('type') as FormControl;
  }

  get amount() {
    return this.formtransaction.get('amount') as FormControl;
  }

  get categoryId() {
    return this.formtransaction.get('categoryId') as FormControl;
  }

  get date() {
    return this.formtransaction.get('date') as FormControl;
  }

  createTransaction(){
    Swal.fire({
      title: '¿Estás seguro?',
      text: "Confirma si deseas registrar la transacción",
      icon: 'question',
      showCancelButton: true,
      confirmButtonColor: '#007B3E',
      cancelButtonColor: '#d33',
      confirmButtonText: 'Si , crear',
      cancelButtonText: 'No, cancelar',
      reverseButtons: true,
      buttonsStyling: true
    }).then((result) => {
      if (result.isConfirmed) {
        if(this.formtransaction.valid){
          this.spinner.show();
          this.transactionService.createTransaction(this.formtransaction.value).subscribe({
            next:(res)=>{
            },
            error:(errorData)=>{
              Swal.fire({
                title: "Error",
                text: "Error al registrar la transacción",
                icon: "error",
                confirmButtonColor: '#007B3E'
              });
              this.spinner.hide();
            },
            complete:()=>{
              window.location.reload();
            }
          });
        }
        else{
          this.formtransaction.markAllAsTouched();
          Swal.fire({
            title: "Error",
            text: "Debes llenar todos los capos obligatorios",
            icon: "warning",
            confirmButtonColor: '#007B3E'
          });
        }
      }});
  }

  //VER TRANSACCION


  seeTransaction(id:number){
    this.spinner.show();
    this.transactionService.seeTransaction(id).subscribe({
      next:(res)=>{
        this.transaction=res;
      },
      error: (errorData)=> {
        Swal.fire({
          title: "Error",
          text: "Error al cargar la información de la transacción",
          icon: "error",
          confirmButtonColor: '#007B3E'
        });
        this.spinner.hide();
      },
      complete: () => {
        this.actualizarFormulario();
        this.spinner.hide();
      }
    });
  }


  //EDITAR TRANSACCION


  idtransaction:number;
  transaction:transaction= new transaction();

  formtransactionUpdate= new FormGroup({
      'type': new FormControl(this.transaction.type, Validators.required),
      'amount': new FormControl(this.transaction.amount, [Validators.required, Validators.pattern(/^\d*\.?\d*$/)]),
      'categoryName': new FormControl(this.transaction.categoryName,Validators.required),
      'categoryId': new FormControl(this.transaction.categoryId),
      'date': new FormControl(this.transaction.date, Validators.required),
      'description': new FormControl(this.transaction.description, Validators.required),
      'id': new FormControl(0)
  });

  get typeUpdate() {
    return this.formtransactionUpdate.get('type') as FormControl;
  }

  get amountUpdate() {
    return this.formtransactionUpdate.get('amount') as FormControl;
  }

  get categoryNameUpdate() {
    return this.formtransactionUpdate.get('categoryName') as FormControl;
  }

  get dateUpdate() {
    return this.formtransactionUpdate.get('date') as FormControl;
  }

  actualizarFormulario(){
    this.formtransactionUpdate.get("type")?.setValue(this.transaction.type);
    this.formtransactionUpdate.get("amount")?.setValue(this.transaction.amount);
    this.formtransactionUpdate.get("categoryId")?.setValue(this.transaction.categoryId);
    this.formtransactionUpdate.get("categoryName")?.setValue(this.transaction.categoryName);
    this.formtransactionUpdate.get("date")?.setValue(this.transaction.date);
    this.formtransactionUpdate.get("description")?.setValue(this.transaction.description);
    this.formtransactionUpdate.get("id")?.setValue(this.transaction.id);
  }

  updateTransaction(id:number){
    this.router.navigate(["/dashboard/updateTransaction", id]);
  }


  //ELIMINAR TRANSACCION
  deleteTransaction(id:number){
    Swal.fire({
      title: '¿Estás seguro?',
      text: "Confirma si deseas eliminar la transacción",
      icon: 'question',
      showCancelButton: true,
      confirmButtonColor: '#007B3E',
      cancelButtonColor: '#d33',
      confirmButtonText: 'Si , crear',
      cancelButtonText: 'No, cancelar',
      reverseButtons: true,
      buttonsStyling: true
    }).then((result) => {
      if (result.isConfirmed) {
        this.spinner.show();
        this.transactionService.deleteTransaction(id).subscribe({
          next:(res)=>{
          },
          error:(errorData)=>{
            Swal.fire({
              title: "Error",
              text: "Error, no se pudo eliminar la transacción",
              icon: "error",
              confirmButtonColor: '#007B3E'
            });
          },
          complete:()=>{
            this.spinner.hide();
            Swal.fire({
              title: "Hecho",
              text: "La transaccion ha sido eliminada",
              icon: "success",
              confirmButtonColor: '#007B3E'
            }).then((result)=>{
              if (result.isConfirmed){
                window.location.reload();
              }
            });
          }
        });
      }});
  }


  //CATEGORIAS

  categories:category[];

  loadCategories(){
    this.spinner.show();
    this.categoryService.listCategories().subscribe({
      next:(res)=>{
        this.categories=res;
      },
      error:(errorData)=>{
        Swal.fire({
          title: "Error",
          text: "Error al cargar las categorías",
          icon: "error",
          confirmButtonColor: '#007B3E'
        });
        this.spinner.hide();
      },
      complete:()=>{
        this.spinner.hide();
      }
    });
  }
}
    <div class="spinner">
        <ngx-spinner
        bdColor="rgba(0, 0, 0, 0)"
        template="<img src='/assets/spinner.gif'/>"
        ></ngx-spinner>
    </div>

<div class="titulo">
    <h2 class="titulo_texto text-center">Lista de transacciones</h2>
    <div>
        <button (click)="createTransactionModal()" data-bs-toggle="modal" data-bs-target="#createTransaction" class="btn btn_aqua">Registrar nueva transacción</button>
    </div>
</div>

<div class="container__table">
    <table id="tabla_clientes" class="table cell-border table-striped hover">
        <thead>
            <tr>
                <th>Categoría</th>
                <th>Cantidad</th>
                <th>Tipo de transacción</th>
                <th>Fecha de la transacción</th>
                <th>Acciones</th>
            </tr>
        </thead>
        <tbody>
            <tr *ngFor="let transaction of transactions">
                <td>{{transaction.categoryName}}</td>
                <td>{{transaction.amount}}</td>
                <td>{{transaction.type}}</td>
                <td>{{transaction.date}}</td>
                <td>
                    <div class="options">
                        <button (click)="seeTransaction(transaction.id)" data-bs-toggle="modal" data-bs-target="#seeTransaction" class="btn btn-primary">Ver</button>
                        <button (click)="updateTransaction(transaction.id)" class="btn btn-success">Editar</button> 
                        <button (click)="deleteTransaction(transaction.id)" class="btn btn-danger" >Eliminar</button>
                    </div>
                </td>
            </tr>
        </tbody>
    </table>
</div>

<div class="modal fade modalContenedor" id="createTransaction" tabindex="-1" aria-labelledby="modalCreate" style="display: none;" aria-hidden="true">
    <div class="modal-dialog modal-lg">
        <div class="modal-content">
            <div class="modal-header titulo--verde">
                <h1 class="modal-title fs-5" id="modalPonentes">Registrar Nueva transacción</h1>
                <i type="button" class="bi bi-x-lg" data-bs-dismiss="modal" aria-label="Close"></i>
            </div>
            <div class="modal-body">
                <form [formGroup]="formtransaction" class="row g-4" >   
                    <div class="row g-4">   
                            <div class="form-group col-md-6">
                                <label class="formulario__label" for=""><b class="formulario__obligatorio">*</b>Tipo de transacción:</label>
                                <select  class="form-select" formControlName="type">
                                    <option value="" selected>Seleccione una opción...</option>
                                    <option value="Ingreso">Ingreso</option>
                                    <option value="Gasto">Gasto</option>
                                </select>
                                <div class="alert alert-danger mt-2" *ngIf="type.invalid && (type.dirty || type.touched)">
                                    Este es un campo obligatorio
                                </div>
                            </div>

                            <div class="form-group col-md-6">
                                <label class="formulario__label" for=""><b class="formulario__obligatorio">*</b>Monto:</label>
                                <input class="form-control" type="text" formControlName="amount">
                                <div *ngIf="amount.invalid && (amount.dirty || amount.touched)" class="alert alert-danger">
                                    <div *ngIf="amount.errors?.['required']">Este es un campo obligatorio.</div>
                                    <div *ngIf="amount.errors?.['pattern']">
                                        Solo puedes incluir valores numéricos o decimales
                                    </div>
                                </div>


                            </div>

                            <div class="form-group col-md-6">
                                <label class="formulario__label" for=""><b class="formulario__obligatorio">*</b>Categoría:</label>
                                <select  class="form-select modal__input" formControlName="categoryId">
                                    <option [ngValue]="null" selected>Seleccione una opción...</option>
                                    <option *ngFor="let category of categories" value={{category.id}}>{{category.name}}</option>
                                </select>
                                <div class="alert alert-danger mt-2" *ngIf="categoryId.invalid && (categoryId.dirty || categoryId.touched)">
                                    Este es un campo obligatorio
                                </div>
                            </div>



                            <div class="form-group col-md-6">
                                <label class="formulario__label" for=""><b class="formulario__obligatorio">*</b>Fecha de registro:</label>
                                <input class="form-control" type="date" formControlName="date">
                                <div class="alert alert-danger mt-2" *ngIf="date.invalid && (date.dirty || date.touched)">
                                    Este es un campo obligatorio
                                </div>
                            </div>

                            <div class="form-group col-md-6">
                                <label class="formulario__label" for="">Descripción:</label>
                                <textarea rows="10" class="form-control" formControlName="description" required >
                                </textarea>
                            </div>
                    </div>
                </form>
            </div>
            <div class="modal-footer">
                <button type="button" class="btn btn-danger" data-bs-dismiss="modal">Cancelar</button>
                <button type="submit" (click)="createTransaction()" class="btn btn-success" data-bs-dismiss="modal">Crear</button>
            </div>
        </div>
    </div>
</div>

<div class="modal fade modalContenedor" id="seeTransaction" tabindex="-1" aria-labelledby="modalSee" style="display: none;" aria-hidden="true">
    <div class="modal-dialog modal-lg">
        <div class="modal-content">
            <div class="modal-header titulo--verde">
                <h1 class="modal-title fs-5" id="modalPonentes">Transacción numero {{idtransaction}}</h1>
                <i type="button" class="bi bi-x-lg" data-bs-dismiss="modal" aria-label="Close"></i>
            </div>
            <div class="modal-body">
                    <div class="row g-4">
                        <div class="form-group col-md-6">
                            <label class="formulario__label" for="nombre">Tipo de transacción:</label>
                            <input class="form-control" type="text" readonly value={{transaction.type}}>
                        </div>

                        <div class="form-group col-md-6">
                            <label class="formulario__label" for="url">Cantidad:</label>
                            <input class="form-control" type="text" readonly value={{transaction.amount}}>
                        </div>

                        <div class="form-group col-md-12">
                            <label class="formulario__label" for="descripcion">Categoría:</label>
                            <textarea class="form-control" type="text" readonly value={{transaction.categoryName}}>
                            </textarea>
                        </div>

                        <div class="form-group col-md-6">
                            <label class="formulario__label" for="fecha">Descripción:</label>
                            <input class="form-control" type="text" readonly value={{transaction.description}}>
                        </div>

                        <div class="form-group col-md-6">
                            <label class="formulario__label" for="">Fecha de la transacción:</label>
                            <input class="form-control" type="date" readonly value={{transaction.date}}>
                        </div>
                    </div>
            </div>
            <div class="modal-footer">
                <button type="button" class="btn btn-danger" data-bs-dismiss="modal">Cerrar</button>
            </div>
        </div>
    </div>
</div>
