import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { HttpClient } from '@angular/common/http';
import { proyectos } from '../models/proyectos';

@Injectable({
  providedIn: 'root'
})
export class ClientesService {

  constructor(private httpClient:HttpClient) {}

  listarClientes(): Observable<proyectos[]>{
    return this.httpClient.get<proyectos[]>("http://localhost:8080/clientes");
  }

  crearCliente(id:number,cliente:any): Observable<proyectos>{
    return this.httpClient.post<proyectos>(`http://localhost:8080/crearcliente/${id}`, cliente);
  }
  editarCliente(id:number,cliente:any): Observable<proyectos>{
    return this.httpClient.put<proyectos>(`http://localhost:8080/editarcliente/${id}`, cliente);
  }
  eliminarCliente(id:number): Observable<proyectos>{
    return this.httpClient.delete<proyectos>(`http://localhost:8080/editarcliente/${id}`);
  }
}
