import { TransactionService } from '../../services/transaction.service';
import { Router } from '@angular/router';
import { transaction } from '../../models/transaction';
import DataTable from 'datatables.net-dt';
import { CategoryoService } from '../../services/category.service';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { category } from '../../models/category';

@Component({
  selector: 'app-transactions-list',
  templateUrl: './transactions-list.component.html',
  styleUrls: ['./transactions-list.component.css']
})
export class TransactionsListComponent {

  transactions:transaction[];

  constructor(private transactionService: TransactionService, private categoryService:CategoryoService, private router:Router){}

  ngOnInit(): void {
    this.listTransactions();
  }

  creteDatatable() {
    setTimeout(() => {
      const table= new DataTable("#tabla_clientes",{
        language: {
          url: '/assets/es-ES.json',
        },
        columnDefs: [
          {
            targets: "_all",
            className: 'dt-center'
          }
        ],
        paging: true,
        pagingType: 'simple',
        layout:{
          topStart: 'div',
          topEnd: 'pageLength',
          bottomStart: 'paging',
          bottomEnd: 'info'
        },
      });
    }, 1);
  }

  listTransactions(){
    this.transactionService.listarTransactions().subscribe({
      next: (res) => {
        this.transactions=res;
      },
      error: (errorData) => {
      alert("Error al cargar los datos")
      },
      complete: () => {
        alert("importacion completada");
        this.creteDatatable();
      }
    });
  }



  deleteTransaction(id:number){
    this.transactionService.deleteTransaction(id).subscribe({
      next:(res)=>{

      },
      error:(errorData)=>{
        alert("Error al eliminar el cliente");
      },
      complete:()=>{
        alert("Cliente eliminado");
      }
    });
  }

  //Crear Transacciones

  categories:category[];
  createTransactionModal(){
    this.importCategories();
  }

  importCategories(){
    this.categoryService.listCategories().subscribe({
      next:(res)=>{
        this.categories=res;
      },
      error:(errorData)=>{
        alert("Error al registrar la transaccion")
      },
      complete:()=>{
        //alert("categorias Importadas");
      }
    });
  }

formtransaction = new FormGroup({
  'type': new FormControl('', Validators.required),
  'amount': new FormControl('', Validators.required),
  'categoryId': new FormControl(null, Validators.required),
  'description': new FormControl('', Validators.required),
  'date': new FormControl(null, Validators.required)
});

createTransaction(){
  if(this.formtransaction.valid){
    this.transactionService.createTransaction(this.formtransaction.value).subscribe({
      next:(res)=>{
      },
      error:(errorData)=>{
        alert("Error al registrar la transaccion")
      },
      complete:()=>{
        alert("transaccion registrada");
      }
    });
  }
  else{
    this.formtransaction.markAllAsTouched();
    alert("Debes llenar todos los campos");
  }
}


//EDITAR TRANSACCION

  updateTransactionModal(id:number){
    this.idtransaction= id;
    this.loadTransaction();
  }

  idtransaction:number;
  transaction:transaction= new transaction();

  loadTransaction(){
    this.transactionService.seeTransaction(this.idtransaction).subscribe({
      next:(res)=>{
        this.transaction=res;
        this.loadCategories();
        this.actualizarFormulario();
      },
      error: (errorData)=> {
        alert("Error al carga la información de la transacción");
      },
      complete: () => {

        alert("Datos cargados");
      }
    });
  }

  loadCategories(){
    this.categoryService.listCategories().subscribe({
      next:(res)=>{
        this.categories=res;
      },
      error:(errorData)=>{
        alert("Error al registrar la transaccion")
      },
      complete:()=>{
        alert("categorias Importadas");
      }
    });
  }


  formtransactionUpdate= new FormGroup({
      'type': new FormControl(this.transaction.type, Validators.required),
      'amount': new FormControl(this.transaction.amount, Validators.required),
      'categoryName': new FormControl(this.transaction.categoryName, Validators.required),
      'categoryId': new FormControl(this.transaction.categoryId, Validators.required),
      'date': new FormControl(this.transaction.date, Validators.required),
      'description': new FormControl(this.transaction.description, Validators.required),
      'id': new FormControl(0)
  });

  actualizarFormulario(){
    this.formtransactionUpdate.get("type")?.setValue(this.transaction.type);
    this.formtransactionUpdate.get("amount")?.setValue(this.transaction.amount);
    this.formtransactionUpdate.get("categoryName")?.setValue(this.transaction.categoryName);
    this.formtransactionUpdate.get("categoryId")?.setValue(this.transaction.categoryId);
    this.formtransactionUpdate.get("date")?.setValue(this.transaction.date);
    this.formtransactionUpdate.get("description")?.setValue(this.transaction.description);
    this.formtransactionUpdate.get("id")?.setValue(this.transaction.id);
    console.log(this.formtransactionUpdate.value);
  }


  updateTransaction(){
    var idcategory:any = this.formtransaction.get('categoryName')?.value;
    this.formtransactionUpdate.get('categoryId')?.setValue(idcategory);
    this.formtransactionUpdate.get('id')?.setValue(this.idtransaction);
    this.transactionService.updateTransaction(this.idtransaction, this.formtransaction.value).subscribe({
      next:(res)=>{
      },
      error:(errorData)=>{
        alert("Error al editar la transacción")
      },
      complete:()=>{
        alert("Transacción editada");
      }
    });
  }

  //VER TRANSACCION
  seeTransactionModal(id:number){
    this.importarProyecto(id);
  }

  importarProyecto(id:number){
    this.transactionService.seeTransaction(id).subscribe({
      next:(res)=>{
        this.transaction=res;
      },
      error: (errorData)=> {
        alert("Error al carga la informacion del proyecto");
      },
      complete: () => {
        alert("Datos cargados");
      }
    });
  }

  backToList(){
    this.router.navigate(["/dashboard/listTransactions"]);
  }
}
<div class="titulo">
    <h2 class="titulo_texto text-center">Lista de transacciones</h2>
    <div id="custom-button-container">
        <button (click)="createTransactionModal()" data-bs-toggle="modal" data-bs-target="#createTransaction" class="btn btn_aqua">Registrar nueva transacción</button>
    </div>
</div>

<div class="container__table">
    <table id="tabla_clientes" class="table cell-border table-striped hover">
        <thead>
            <tr>
                <th>Categoría</th>
                <th>Cantidad</th>
                <th>Tipo de transacción</th>
                <th>Fecha de la transacción</th>
                <th>Acciones</th>
            </tr>
        </thead>
        <tbody>
            <tr *ngFor="let transaction of transactions">
                <td>{{transaction.categoryName}}</td>
                <td>{{transaction.amount}}</td>
                <td>{{transaction.type}}</td>
                <td>{{transaction.date}}</td>
                <td>
                    <div class="options">
                        <button (click)="seeTransactionModal(transaction.id)" data-bs-toggle="modal" data-bs-target="#seeTransaction" class="btn btn-primary">Ver</button>
                        <button (click)="updateTransactionModal(transaction.id)" data-bs-toggle="modal" data-bs-target="#updateTransaction" class="btn btn-success">Editar</button> 
                        <button (click)="deleteTransaction(transaction.id)" class="btn btn-danger" >Eliminar</button>
                    </div>
                </td>
            </tr>
        </tbody>
    </table>
</div>

<div class="modal fade modalContenedor" id="createTransaction" tabindex="-1" aria-labelledby="modalCreate" style="display: none;" aria-hidden="true">
    <div class="modal-dialog">
        <div class="modal-content">
            <form [formGroup]="formtransaction">
                <div class="modal-header titulo--verde">
                    <h1 class="modal-title fs-5" id="modalPonentes">Registrar Nueva transacción</h1>
                    <i type="button" class="bi bi-x-lg" data-bs-dismiss="modal" aria-label="Close"></i>
                </div>
                <div class="modal-body">
                        <div class="row g-4">   
                            <div class="container row">
                                <div class="form-group col-md-6">
                                    <label for="">Nombre:</label>
                                    <input class="form-control" type="text" formControlName="type">
                                </div>

                                <div class="form-group col-md-6">
                                    <label for="">Cantidad</label>
                                    <input class="form-control" type="text" formControlName="amount">
                                </div>

                                <div class="form-group col-md-6">
                                    <label for="">Categoría:</label>
                                    <select  class="form-select modal__input" formControlName="categoryId">
                                        <option [ngValue]="null" selected>Seleccione una opción...</option>
                                        <option *ngFor="let category of categories" value={{category.id}}>{{category.name}}</option>
                                    </select>
                                </div>

                                <div class="form-group col-md-6">
                                    <label for="">Descripción:</label>
                                    <input class="form-control" type="text" formControlName="description">
                                </div>

                                <div class="form-group col-md-6">
                                    <label for="">Fecha de inicio:</label>
                                    <input class="form-control" type="date" formControlName="date">
                                </div>
                            </div>
                        </div>
                </div>
                <div class="modal-footer">
                    <button type="button" class="btn btn-danger" data-bs-dismiss="modal">Cancelar</button>
                    <button type="submit" (click)="createTransaction()" class="btn btn-success" data-bs-dismiss="modal">Crear</button>
                </div>
            </form>
        </div>
    </div>
</div>

<div class="modal fade modalContenedor" id="seeTransaction" tabindex="-1" aria-labelledby="modalSee" style="display: none;" aria-hidden="true">
    <div class="modal-dialog">
        <div class="modal-content">
            <div class="modal-header titulo--verde">
                <h1 class="modal-title fs-5" id="modalPonentes">Transacción numero {{idtransaction}}</h1>
                <i type="button" class="bi bi-x-lg" data-bs-dismiss="modal" aria-label="Close"></i>
            </div>
            <div class="modal-body">
                    <div class="row g-4">
                        <div class="form-group col-md-6">
                            <label for="nombre">Tipo de transacción:</label>
                            <input class="form-control" type="text" readonly value={{transaction.type}}>
                        </div>

                        <div class="form-group col-md-6">
                            <label for="url">Cantidad:</label>
                            <input class="form-control" type="text" readonly value={{transaction.amount}}>
                        </div>

                        <div class="form-group col-md-12">
                            <label for="descripcion">Categoría:</label>
                            <textarea class="form-control" type="text" readonly value={{transaction.categoryName}}>
                            </textarea>
                        </div>

                        <div class="form-group col-md-6">
                            <label for="fecha">Descripción:</label>
                            <input class="form-control" type="text" readonly value={{transaction.description}}>
                        </div>

                        <div class="form-group col-md-6">
                            <label for="">Fecha de la transacción:</label>
                            <input class="form-control" type="date" readonly value={{transaction.date}}>
                        </div>
                    </div>
            </div>
            <div class="modal-footer">
                <button type="button" class="btn btn-danger" data-bs-dismiss="modal">Cerrar</button>
            </div>
        </div>
    </div>
</div>

<div class="modal fade modalContenedor" id="updateTransaction" tabindex="-1" aria-labelledby="modalUpdate" style="display: none;" aria-hidden="true">
    <div class="modal-dialog">
        <div class="modal-content">
            <div class="modal-header titulo--verde">
                <h1 class="modal-title fs-5" id="modalPonentes">transacción numero {{idtransaction}}</h1>
                <i type="button" class="bi bi-x-lg" data-bs-dismiss="modal" aria-label="Close"></i>
            </div>
            <div class="modal-body">
                <form #empleadoForm="ngForm" class="row g-4" [formGroup]="formtransactionUpdate">
                    <div class="row g-4">   
                        <div class="form-group col-md-6">
                            <label for="nombre">Tipo de transacción</label>
                            <input class="form-control" type="text" formControlName="type">
                        </div>

                        <div class="form-group col-md-6">
                            <label for="url">Cantidad</label>
                            <input class="form-control" type="text" formControlName="amount">
                        </div>

                        <div class="form-group col-md-6">
                            <label for="descripcion">Categoría:</label>
                            <select  class="form-select modal__input" formControlName="categoryName">
                                <option *ngFor="let category of categories" value={{category.id}}>{{category.name}}</option>
                            </select>
                        </div>

                        <div class="form-group col-md-6">
                            <label for="fecha">Fecha de transacción:</label>
                            <input class="form-control" type="date" formControlName="date">
                        </div>

                        <div class="form-group col-md-6">
                            <label for="">Descripción:</label>
                            <input class="form-control" type="text"  formControlName="description">
                        </div>
                    </div>
                </form>
            </div>
            <div class="modal-footer">
                <button data-bs-dismiss="modal" class="btn btn-primary">Volver</button>
                <button (click)="updateTransaction()" class="btn btn-success">Editar</button>
            </div>
        </div>
    </div>
</div>
