import { Component } from '@angular/core';
import { TransactionService } from '../../services/transaction.service';
import { Router } from '@angular/router';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { CategoryoService } from '../../services/category.service';
import { category } from '../../models/category';

@Component({
  selector: 'app-create-transaction',
  templateUrl: './create-transaction.component.html',
  styleUrls: ['./create-transaction.component.css']
})
export class CreateTransactionComponent {
  categories:category[];
  constructor(private transactionService:TransactionService, private categoryService:CategoryoService, private router:Router){}

  ngOnInit(): void {
    this.importCategories();
  }

    importCategories(){
      this.categoryService.listCategories().subscribe({
        next:(res)=>{
          this.categories=res;
        },
        error:(errorData)=>{
          alert("Error al registrar la transaccion")
        },
        complete:()=>{
          //alert("categorias Importadas");
        }
      });
    }

  formtransaction = new FormGroup({
    'type': new FormControl('', Validators.required),
    'amount': new FormControl('', Validators.required),
    'categoryId': new FormControl(null, Validators.required),
    'description': new FormControl('', Validators.required),
    'date': new FormControl(null, Validators.required)
  });

  createTransaction(){
    if(this.formtransaction.valid){
      this.transactionService.createTransaction(this.formtransaction.value).subscribe({
        next:(res)=>{
        },
        error:(errorData)=>{
          alert("Error al registrar la transaccion")
        },
        complete:()=>{
          alert("transaccion registrada");
        }
      });
    }
    else{
      this.formtransaction.markAllAsTouched();
      alert("Debes llenar todos los campos");
    }
  }

  backToList(){
    this.router.navigate(["/dashboard/listTransactions"]);
  }
}
<div class="titulo">
    <h2 class="text-center">Registrar Nueva transacción</h2>
</div>
<form [formGroup]="formtransaction">
    <div class="container row">
        <div class="form-group col-md-6">
            <label for="">Nombre:</label>
            <input class="form-control" type="text" formControlName="type">
        </div>

        <div class="form-group col-md-6">
            <label for="">Cantidad</label>
            <input class="form-control" type="text" formControlName="amount">
        </div>

        <div class="form-group col-md-6">
            <label for="">Categoría:</label>
            <select  class="form-select modal__input" formControlName="categoryId">
                <option [ngValue]="null" selected>Seleccione una opción...</option>
                <option *ngFor="let category of categories" value={{category.id}}>{{category.name}}</option>
            </select>
        </div>

        <div class="form-group col-md-6">
            <label for="">Descripción:</label>
            <input class="form-control" type="text" formControlName="description">
        </div>

        <div class="form-group col-md-6">
            <label for="">Fecha de inicio:</label>
            <input class="form-control" type="date" formControlName="date">
        </div>
    </div>

    <div class="buttons form-group">
        <button (click)="backToList()" class="btn btn-danger">Cancelar</button>
        <button (click)="createTransaction()" class="btn btn-primary">Crear Proyecto</button>
    </div>
</form>
