import { Component } from '@angular/core';
import DataTable from 'datatables.net-dt';
@Component({
  selector: 'app-listaclientes',
  templateUrl: './listaclientes.component.html',
  styleUrls: ['./listaclientes.component.css']
})



export class ListaclientesComponent {

  ngOnInit(): void {
    this.crearDatatable();
  }

  crearDatatable() {
    setTimeout(() => {
      const table= new DataTable("#tabla_clientes",{
        language: {
          url: '/assets/es-ES.json',
        },
        columnDefs: [
          {
            targets: "_all",
            className: 'dt-center'
          }
        ],
        paging: true,
        pagingType: 'simple'
      });
    }, 1);
  }
}
<h2 class="text-center">Lista de clientes</h2>
<table id="tabla_clientes" class="table">
    <thead  class="cell-border table-striped stripe hover">
        <tr>
            <th>Nombre</th>
            <th>Apellido</th>
            <th>Dirección</th>
            <th>Teléfono</th>
            <th>Estado</th>
            <th>Acciones</th>
        </tr>
    </thead>
    <tbody>
        <tr>
            <td></td>
        </tr>
    </tbody>
</table>
