import { Component } from '@angular/core';
import { proyectos } from '../../models/proyectos';
import { ClientesService } from '../../services/clientes.service';
import { ActivatedRoute, Router } from '@angular/router';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-editarproyecto',
  templateUrl: './editarproyecto.component.html',
  styleUrls: ['./editarproyecto.component.css']
})
export class EditarproyectoComponent {

  idproyecto:number;
  proyecto:proyectos= new proyectos();
  constructor(private proyectoServicio:ClientesService,private router:Router, private route:ActivatedRoute){}
  ngOnInit(): void {
    this.idproyecto= this.route.snapshot.params["id"];
    this.importarProyecto()
  }

  formproyecto= new FormGroup({
    'nombre': new FormControl(this.proyecto.nombre, Validators.required),
      'descripcion': new FormControl(this.proyecto.descripcion, Validators.required),
      'url': new FormControl(this.proyecto.url, Validators.required),
      'fechaCreacion': new FormControl(this.proyecto.fechaCreacion, Validators.required),
      'nombreUsuario': new FormControl(this.proyecto.nombreUsuario)
  });

  actualizarFormulario(){
    this.formproyecto = new FormGroup ({
      'nombre': new FormControl(this.proyecto.nombre, Validators.required),
      'descripcion': new FormControl(this.proyecto.descripcion, Validators.required),
      'url': new FormControl(this.proyecto.url, Validators.required),
      'fechaCreacion': new FormControl(this.proyecto.fechaCreacion, Validators.required),
      'nombreUsuario': new FormControl(this.proyecto.nombreUsuario, Validators.required)
    });
  }

  importarProyecto(){
    this.proyectoServicio.verProyecto(this.idproyecto).subscribe({
      next:(res)=>{
        this.proyecto=res;
      },
      error: (errorData)=> {
        alert("Error al carga la informacion del proyecto");
      },
      complete: () => {
        this.actualizarFormulario();
        alert("Datos cargados");
      }
    });
  }

  volverInicio(){
    this.router.navigate(["/dashboard/listaproyectos"]);
  }

  editarProyecto(){
    this.proyectoServicio.editarProyecto(this.idproyecto, this.formproyecto.value).subscribe({
      next:(res)=>{
      },
      error:(errorData)=>{
        alert("Error al editar el cliente")
      },
      complete:()=>{
        alert("Cliente editado");
      }
    });
  }
}
<div class="titulo">
    <h2 class="text-center">Convocatoria numero {{idproyecto}}</h2>
</div>

<form [formGroup]="formproyecto">
    <div class="container row">
        <div class="form-group col-md-6">
            <label for="nombre">Nombre:</label>
            <input class="form-control" type="text" formControlName="nombre">
        </div>

        <div class="form-group col-md-6">
            <label for="url">URL:</label>
            <input class="form-control" type="text" formControlName="url">
        </div>

        <div class="form-group col-md-12">
            <label for="descripcion">Descripción:</label>
            <textarea class="form-control" type="text" formControlName="descripcion">
            </textarea>
        </div>

        <div class="form-group col-md-6">
            <label for="fecha">Fecha de inicio:</label>
            <input class="form-control" type="date" formControlName="fechaCreacion">
        </div>

        <div class="form-group col-md-6">
            <label for="">Creado por:</label>
            <input class="form-control" type="text" readonly formControlName="nombreUsuario">
        </div>

        <div class="buttons form-group">
            <button (click)="volverInicio()" class="btn btn-primary">Volver</button>
            <button (click)="editarProyecto()" class="btn btn-success">Editar</button>
        </div>
    </div>
</form>
